insert into faqs (id, faq_order, question, answer)
values  ('57f9aafc-a541-45d7-bb73-f191d433aff0', 0, 'Как записаться на консультацию по направлению из поликлиники?', 'Записаться на прием можно по круглосуточному тел 8-495-959-88-00, 8-495-959-88-03. Также можно воспользоваться формой обратной связи в разделе «Запись на приём»'),
        ('f54b3848-666d-42d5-a458-9b8578cef1e5', 1, 'Как перенести (изменить) дату госпитализации по ОМС?', 'Для изменения даты госпитализации необходимо связаться с профильным отделением. Контактная информация в разделе «Запись на плановую госпитализацию по полису ОМС».'),
        ('587f4eb3-7bb2-4e96-aad3-71b208ff2873', 2, 'Что нужно для госпитализации иногородних по полису ОМС?', 'Основанием для плановой госпитализации иногороднего ребенка в нашу больницу по полису ОМС служит направление из поликлиники (форма 057/у-04) по месту наблюдения пациента или направление из территориального органа управления здравоохранением по месту жительства с указанием источника финансирования. Также можно обратиться в проект «Москва-столица здоровья» тел. проекта 8 (495) 587-70-88. Подробности о госпитализации размещены на нашем сайте в разделе «Госпитализация».'),
        ('9f5bbf80-94aa-459f-a407-1b2fe69c5bf3', 3, 'Возможна ли плановая госпитализация', 'Для плановой госпитализации необходимо наличие всех анализов и документов с соблюдением сроков'),
        ('1829a232-75ab-457b-a243-aee9b0b6916e', 4, 'Нельзя госпитализировать ребенка, если вакцинация проведена менее, чем за месяц до госпитализации (реакция Манту и Диаскин-тест вакцинацией не являются).', 'Нельзя госпитализировать ребенка, если вакцинация проведена менее, чем за месяц до госпитализации (реакция Манту и Диаскин-тест вакцинацией не являются).'),
        ('dadf9d1a-9e15-478e-a8f1-73dfdde81123', 5, 'Как в больнице пройти полное обследование?', 'Полное комплексное обследование можно пройти в консультативном центре Морозовской больницы в течение 1 суток. Обследование проводится в амбулаторном режиме на платной основе в рамках программы check-up. Можно использовать готовую диагностическую программу или составить индивидуальную. Для получения услуги необходимо связаться с нашими специалистами по тел. 8-495-959-88-01'),
        ('987aae64-04ed-4f50-b716-783920005f30', 6, 'Как позвонить в отделение и получить информацию о состоянии ребенка?', 'Информацию могут получить только законные представители ребенка при личном обращении к лечащему врачу или заведующему отделением. По телефону медицинский персонал не представляет информацию о находящихся в стационаре пациентах (Федеральный закон «Об основах охраны здоровья граждан в Российской Федерации» от 21 ноября 2011 г. № 323-ФЗ, Федеральный закон «О персональных данных» от 27.07.2006 N 152-ФЗ).'),
        ('6469ad5d-b9a3-451b-981c-05bae8d917e0', 7, 'Есть ли парковка на территории больницы?', 'Ознакомиться с условиями пропуска личного транспорта на территорию больницы можно в разделе «Правила въезда и парковки»'),
        ('59b40692-e7fd-4782-b4e9-0536971488cc', 8, 'За какое время до госпитализации нужно оформить направление на госпитализацию (Форма 057/у-04)?', 'Направление (Форма 057/у-04) рекомендуем получить не ранее');