insert into public.dpo_courses (id, name, description, is_nmo, link_nmo, dpo_course_order, listeners, hours, cost)
values  ('0357b063-992e-4537-809a-10964e93fa28', 'Эхокардиография в педиатрии', '<p>Гинекологическое отделение — единственное в г. Москве отделение для оказания экстренной и плановой медицинской помощи детям и подросткам с гинекологической патологией, проживающим в Москве, Московской области и регионах.;</p><p>Отделение рассчитано на 20 коек, в том числе детей раннего возраста с совместным пребыванием одного из родителей.;</p><p>В нашем отделении получают лечение дети до 18 лет со всеми гинекологическими заболеваниями и пороками половой системы.;</p><p>Проблема патологии репродуктивной системы в неонатальном и раннем детском возрасте – это нарушение формирования пола, связанное как с эндокринными нарушениями, новообразованиями, так и с ферментопатиями, генетическими мутациями; пороки развития наружных и внутренних гениталий, промежности, часто сочетанные с другими пороками развития. В более старшем возрасте у пациентов с патологией репродуктивной системы продолжается медикаментозная и хирургическая коррекция пороков развития, комплексная послеоперационная адаптация в выбранном поле при нарушениях его формирования.;</p><p>За год проводится более 50 реконструктивных операций при пороках развития органов малого таза. Используются уникальные методики, разработанные в ГБУЗ «Морозовская ДГКБ ДЗМ» во время проведения операций по коррекции пола.;</p><p>Наблюдаются дети и подростки с нарушениями полового созревания, дисменорреями, маточными кровотечениями, опухолями и опухолевидными образованиями придатков матки. Оказывается помощь детям с травматическими поражениями наружных половых органов, острыми и хроническими заболеваниями внутренних и наружных половых органов.;</p><p>Также получают лечение девочки-подростки с заболеваниями молочных желез (нелактационный мастит, эктазия протоков, солитарные и множественные кисты, фиброаденомы, пороки развития).;</p><p>Гинекологическое отделение МДГКБ является клинической базой кафедры репродуктивной медицины и хирургии ФПДО МГМСУ (зав. кафедрой — академик РАМН, главный внештатный специалист по акушерству и гинекологии МЗ РФ, профессор, д.м.н. Л.В. Адамян). Сотрудники отделения активно занимаются научной деятельностью, регулярно публикуются в журналах и принимают участия в научных конференциях.;</p><p>Отделение полностью оснащено. Все операции в гинекологическом отделении выполняются с использованием высоких технологий — современных эндоскопических методик.</p>', true, 'http://xn--90adclrioar.xn--p1ai/wp-content/uploads/2021/09/%D0%98%D0%BD%D1%81%D1%82%D1%80%D1%83%D0%BA%D1%86%D0%B8%D1%8F-%D0%BF%D0%BE-%D0%B7%D0%B0%D0%BF%D0%B8%D1%81%D0%B8-%D0%BD%D0%B0-%D1%86%D0%B8%D0%BA%D0%BB-%D0%9D%D0%9C%D0%9E.pdf', 10, 72, 72, 100000),
        ('b7d717b3-1171-4611-9d6b-f1ba3889005b', 'Избранные вопросы детской нефрологии', '<p>Гинекологическое отделение — единственное в г. Москве отделение для оказания экстренной и плановой медицинской помощи детям и подросткам с гинекологической патологией, проживающим в Москве, Московской области и регионах.;</p><p>Отделение рассчитано на 20 коек, в том числе детей раннего возраста с совместным пребыванием одного из родителей.;</p><p>В нашем отделении получают лечение дети до 18 лет со всеми гинекологическими заболеваниями и пороками половой системы.;</p><p>Проблема патологии репродуктивной системы в неонатальном и раннем детском возрасте – это нарушение формирования пола, связанное как с эндокринными нарушениями, новообразованиями, так и с ферментопатиями, генетическими мутациями; пороки развития наружных и внутренних гениталий, промежности, часто сочетанные с другими пороками развития. В более старшем возрасте у пациентов с патологией репродуктивной системы продолжается медикаментозная и хирургическая коррекция пороков развития, комплексная послеоперационная адаптация в выбранном поле при нарушениях его формирования.;</p><p>За год проводится более 50 реконструктивных операций при пороках развития органов малого таза. Используются уникальные методики, разработанные в ГБУЗ «Морозовская ДГКБ ДЗМ» во время проведения операций по коррекции пола.;</p><p>Наблюдаются дети и подростки с нарушениями полового созревания, дисменорреями, маточными кровотечениями, опухолями и опухолевидными образованиями придатков матки. Оказывается помощь детям с травматическими поражениями наружных половых органов, острыми и хроническими заболеваниями внутренних и наружных половых органов.;</p><p>Также получают лечение девочки-подростки с заболеваниями молочных желез (нелактационный мастит, эктазия протоков, солитарные и множественные кисты, фиброаденомы, пороки развития).;</p><p>Гинекологическое отделение МДГКБ является клинической базой кафедры репродуктивной медицины и хирургии ФПДО МГМСУ (зав. кафедрой — академик РАМН, главный внештатный специалист по акушерству и гинекологии МЗ РФ, профессор, д.м.н. Л.В. Адамян). Сотрудники отделения активно занимаются научной деятельностью, регулярно публикуются в журналах и принимают участия в научных конференциях.;</p><p>Отделение полностью оснащено. Все операции в гинекологическом отделении выполняются с использованием высоких технологий — современных эндоскопических методик.</p>', true, 'http://xn--90adclrioar.xn--p1ai/wp-content/uploads/2021/09/%D0%98%D0%BD%D1%81%D1%82%D1%80%D1%83%D0%BA%D1%86%D0%B8%D1%8F-%D0%BF%D0%BE-%D0%B7%D0%B0%D0%BF%D0%B8%D1%81%D0%B8-%D0%BD%D0%B0-%D1%86%D0%B8%D0%BA%D0%BB-%D0%9D%D0%9C%D0%9E.pdf', 2, 72, 72, 20000),
        ('aa7c0f93-ed8f-4bc9-90d9-4546287bd04c', 'Воспалительные заболевания суставов у детей', '<p>Гинекологическое отделение — единственное в г. Москве отделение для оказания экстренной и плановой медицинской помощи детям и подросткам с гинекологической патологией, проживающим в Москве, Московской области и регионах.;</p><p>Отделение рассчитано на 20 коек, в том числе детей раннего возраста с совместным пребыванием одного из родителей.;</p><p>В нашем отделении получают лечение дети до 18 лет со всеми гинекологическими заболеваниями и пороками половой системы.;</p><p>Проблема патологии репродуктивной системы в неонатальном и раннем детском возрасте – это нарушение формирования пола, связанное как с эндокринными нарушениями, новообразованиями, так и с ферментопатиями, генетическими мутациями; пороки развития наружных и внутренних гениталий, промежности, часто сочетанные с другими пороками развития. В более старшем возрасте у пациентов с патологией репродуктивной системы продолжается медикаментозная и хирургическая коррекция пороков развития, комплексная послеоперационная адаптация в выбранном поле при нарушениях его формирования.;</p><p>За год проводится более 50 реконструктивных операций при пороках развития органов малого таза. Используются уникальные методики, разработанные в ГБУЗ «Морозовская ДГКБ ДЗМ» во время проведения операций по коррекции пола.;</p><p>Наблюдаются дети и подростки с нарушениями полового созревания, дисменорреями, маточными кровотечениями, опухолями и опухолевидными образованиями придатков матки. Оказывается помощь детям с травматическими поражениями наружных половых органов, острыми и хроническими заболеваниями внутренних и наружных половых органов.;</p><p>Также получают лечение девочки-подростки с заболеваниями молочных желез (нелактационный мастит, эктазия протоков, солитарные и множественные кисты, фиброаденомы, пороки развития).;</p><p>Гинекологическое отделение МДГКБ является клинической базой кафедры репродуктивной медицины и хирургии ФПДО МГМСУ (зав. кафедрой — академик РАМН, главный внештатный специалист по акушерству и гинекологии МЗ РФ, профессор, д.м.н. Л.В. Адамян). Сотрудники отделения активно занимаются научной деятельностью, регулярно публикуются в журналах и принимают участия в научных конференциях.;</p><p>Отделение полностью оснащено. Все операции в гинекологическом отделении выполняются с использованием высоких технологий — современных эндоскопических методик.</p>', true, 'http://xn--90adclrioar.xn--p1ai/wp-content/uploads/2021/09/%D0%98%D0%BD%D1%81%D1%82%D1%80%D1%83%D0%BA%D1%86%D0%B8%D1%8F-%D0%BF%D0%BE-%D0%B7%D0%B0%D0%BF%D0%B8%D1%81%D0%B8-%D0%BD%D0%B0-%D1%86%D0%B8%D0%BA%D0%BB-%D0%9D%D0%9C%D0%9E.pdf', 9, 36, 36, 90000),
        ('6e147888-cd8c-4eda-ab9d-e669fa92a395', 'Актуальные вопросы трансфузиологии', '<p>Гинекологическое отделение — единственное в г. Москве отделение для оказания экстренной и плановой медицинской помощи детям и подросткам с гинекологической патологией, проживающим в Москве, Московской области и регионах.;</p><p>Отделение рассчитано на 20 коек, в том числе детей раннего возраста с совместным пребыванием одного из родителей.;</p><p>В нашем отделении получают лечение дети до 18 лет со всеми гинекологическими заболеваниями и пороками половой системы.;</p><p>Проблема патологии репродуктивной системы в неонатальном и раннем детском возрасте – это нарушение формирования пола, связанное как с эндокринными нарушениями, новообразованиями, так и с ферментопатиями, генетическими мутациями; пороки развития наружных и внутренних гениталий, промежности, часто сочетанные с другими пороками развития. В более старшем возрасте у пациентов с патологией репродуктивной системы продолжается медикаментозная и хирургическая коррекция пороков развития, комплексная послеоперационная адаптация в выбранном поле при нарушениях его формирования.;</p><p>За год проводится более 50 реконструктивных операций при пороках развития органов малого таза. Используются уникальные методики, разработанные в ГБУЗ «Морозовская ДГКБ ДЗМ» во время проведения операций по коррекции пола.;</p><p>Наблюдаются дети и подростки с нарушениями полового созревания, дисменорреями, маточными кровотечениями, опухолями и опухолевидными образованиями придатков матки. Оказывается помощь детям с травматическими поражениями наружных половых органов, острыми и хроническими заболеваниями внутренних и наружных половых органов.;</p><p>Также получают лечение девочки-подростки с заболеваниями молочных желез (нелактационный мастит, эктазия протоков, солитарные и множественные кисты, фиброаденомы, пороки развития).;</p><p>Гинекологическое отделение МДГКБ является клинической базой кафедры репродуктивной медицины и хирургии ФПДО МГМСУ (зав. кафедрой — академик РАМН, главный внештатный специалист по акушерству и гинекологии МЗ РФ, профессор, д.м.н. Л.В. Адамян). Сотрудники отделения активно занимаются научной деятельностью, регулярно публикуются в журналах и принимают участия в научных конференциях.;</p><p>Отделение полностью оснащено. Все операции в гинекологическом отделении выполняются с использованием высоких технологий — современных эндоскопических методик.</p>', true, 'http://xn--90adclrioar.xn--p1ai/wp-content/uploads/2021/09/%D0%98%D0%BD%D1%81%D1%82%D1%80%D1%83%D0%BA%D1%86%D0%B8%D1%8F-%D0%BF%D0%BE-%D0%B7%D0%B0%D0%BF%D0%B8%D1%81%D0%B8-%D0%BD%D0%B0-%D1%86%D0%B8%D0%BA%D0%BB-%D0%9D%D0%9C%D0%9E.pdf', 6, 72, 72, 60000),
        ('45c77c9f-afa4-4af9-8499-1ced7f8cfb72', 'Острые нарушения мозгового кровообращения у детей и подростков. клиника, диагностика и лечение', '<p>Гинекологическое отделение — единственное в г. Москве отделение для оказания экстренной и плановой медицинской помощи детям и подросткам с гинекологической патологией, проживающим в Москве, Московской области и регионах.;</p><p>Отделение рассчитано на 20 коек, в том числе детей раннего возраста с совместным пребыванием одного из родителей.;</p><p>В нашем отделении получают лечение дети до 18 лет со всеми гинекологическими заболеваниями и пороками половой системы.;</p><p>Проблема патологии репродуктивной системы в неонатальном и раннем детском возрасте – это нарушение формирования пола, связанное как с эндокринными нарушениями, новообразованиями, так и с ферментопатиями, генетическими мутациями; пороки развития наружных и внутренних гениталий, промежности, часто сочетанные с другими пороками развития. В более старшем возрасте у пациентов с патологией репродуктивной системы продолжается медикаментозная и хирургическая коррекция пороков развития, комплексная послеоперационная адаптация в выбранном поле при нарушениях его формирования.;</p><p>За год проводится более 50 реконструктивных операций при пороках развития органов малого таза. Используются уникальные методики, разработанные в ГБУЗ «Морозовская ДГКБ ДЗМ» во время проведения операций по коррекции пола.;</p><p>Наблюдаются дети и подростки с нарушениями полового созревания, дисменорреями, маточными кровотечениями, опухолями и опухолевидными образованиями придатков матки. Оказывается помощь детям с травматическими поражениями наружных половых органов, острыми и хроническими заболеваниями внутренних и наружных половых органов.;</p><p>Также получают лечение девочки-подростки с заболеваниями молочных желез (нелактационный мастит, эктазия протоков, солитарные и множественные кисты, фиброаденомы, пороки развития).;</p><p>Гинекологическое отделение МДГКБ является клинической базой кафедры репродуктивной медицины и хирургии ФПДО МГМСУ (зав. кафедрой — академик РАМН, главный внештатный специалист по акушерству и гинекологии МЗ РФ, профессор, д.м.н. Л.В. Адамян). Сотрудники отделения активно занимаются научной деятельностью, регулярно публикуются в журналах и принимают участия в научных конференциях.;</p><p>Отделение полностью оснащено. Все операции в гинекологическом отделении выполняются с использованием высоких технологий — современных эндоскопических методик.</p>', true, 'http://xn--90adclrioar.xn--p1ai/wp-content/uploads/2021/09/%D0%98%D0%BD%D1%81%D1%82%D1%80%D1%83%D0%BA%D1%86%D0%B8%D1%8F-%D0%BF%D0%BE-%D0%B7%D0%B0%D0%BF%D0%B8%D1%81%D0%B8-%D0%BD%D0%B0-%D1%86%D0%B8%D0%BA%D0%BB-%D0%9D%D0%9C%D0%9E.pdf', 3, 36, 36, 30000),
        ('a366183e-d738-4642-8f78-ef880625f8ef', 'Актуальные вопросы детской пульмонологии', '<p>Гинекологическое отделение — единственное в г. Москве отделение для оказания экстренной и плановой медицинской помощи детям и подросткам с гинекологической патологией, проживающим в Москве, Московской области и регионах.;</p><p>Отделение рассчитано на 20 коек, в том числе детей раннего возраста с совместным пребыванием одного из родителей.;</p><p>В нашем отделении получают лечение дети до 18 лет со всеми гинекологическими заболеваниями и пороками половой системы.;</p><p>Проблема патологии репродуктивной системы в неонатальном и раннем детском возрасте – это нарушение формирования пола, связанное как с эндокринными нарушениями, новообразованиями, так и с ферментопатиями, генетическими мутациями; пороки развития наружных и внутренних гениталий, промежности, часто сочетанные с другими пороками развития. В более старшем возрасте у пациентов с патологией репродуктивной системы продолжается медикаментозная и хирургическая коррекция пороков развития, комплексная послеоперационная адаптация в выбранном поле при нарушениях его формирования.;</p><p>За год проводится более 50 реконструктивных операций при пороках развития органов малого таза. Используются уникальные методики, разработанные в ГБУЗ «Морозовская ДГКБ ДЗМ» во время проведения операций по коррекции пола.;</p><p>Наблюдаются дети и подростки с нарушениями полового созревания, дисменорреями, маточными кровотечениями, опухолями и опухолевидными образованиями придатков матки. Оказывается помощь детям с травматическими поражениями наружных половых органов, острыми и хроническими заболеваниями внутренних и наружных половых органов.;</p><p>Также получают лечение девочки-подростки с заболеваниями молочных желез (нелактационный мастит, эктазия протоков, солитарные и множественные кисты, фиброаденомы, пороки развития).;</p><p>Гинекологическое отделение МДГКБ является клинической базой кафедры репродуктивной медицины и хирургии ФПДО МГМСУ (зав. кафедрой — академик РАМН, главный внештатный специалист по акушерству и гинекологии МЗ РФ, профессор, д.м.н. Л.В. Адамян). Сотрудники отделения активно занимаются научной деятельностью, регулярно публикуются в журналах и принимают участия в научных конференциях.;</p><p>Отделение полностью оснащено. Все операции в гинекологическом отделении выполняются с использованием высоких технологий — современных эндоскопических методик.</p>', true, 'http://xn--90adclrioar.xn--p1ai/wp-content/uploads/2021/09/%D0%98%D0%BD%D1%81%D1%82%D1%80%D1%83%D0%BA%D1%86%D0%B8%D1%8F-%D0%BF%D0%BE-%D0%B7%D0%B0%D0%BF%D0%B8%D1%81%D0%B8-%D0%BD%D0%B0-%D1%86%D0%B8%D0%BA%D0%BB-%D0%9D%D0%9C%D0%9E.pdf', 1, 36, 36, 10000),
